mapper.update:
update t_crop_type
set name = #{name}
where id = #{id};

mapper.select:
select id, name, create_time createTime, update_time updateTime
from t_crop_type
where name = #{name}

select id, name, create_time createTime, update_time updateTime
from t_crop_type
where id = #{typeId}

select id, type_id typeId, name, create_time createTime, update_time updateTime
from t_crop_variety
where type_id = #{typeId} and name = #{cropVarietyName}
