mapper.update:
update t_crop_type
set name = #{name}, name_en = #{nameEn}
where id = #{id};

update t_crop_variety 
set name = #{name}, name_en = #{nameEn}
where id = #{id}

mapper.select:
select id, name, name_en, create_user, is_manager, create_time, update_time
from t_crop_type
where name = #{name}

select id, name, name_en, create_user, is_manager, create_time, update_time
from t_crop_type
where id = #{typeId}

select id, type_id, name, name_en, create_user, is_manager, create_time, update_time
from t_crop_variety
where type_id = #{typeId} and name = #{cropVarietyName}
