select id, user_id, survey_time, lat, lng, crop_type, crop_variety, disease_type, disease_severity, pest_type, pest_severity, drought_severity, create_time, update_time
from t_record
where id = #{id}

select count(1)
from t_record
where user_id = #{userId}

select id, user_id, survey_time, lat, lng, crop_type, crop_variety, disease_type, disease_severity, pest_type, pest_severity, drought_severity, create_time, update_time
from t_record
where user_id = #{userId}
order by create_time desc
limit #{start}, #{pageSize}

